import react, { useState } from "react";
import "./expenseForm.css"

export const  ExpenseForm=(props)=>{

    let newList={
        id:"",
        title:"",
        price:"",
        date:""
    }

    const [value, setvalue]= useState(newList)
    const submitHandler=(event)=>{
        event.preventDefault()
        props.value(false)
        if(!value.id || !value.title || !value.price || !value.date){
            alert("fill the fields first")
        }else
        props.list.push(newList)
    }


    let handleChange=(event)=>{

        setvalue( {...value,[event.target.name]: event.target.name==="date" ? new Date (event.target.value) : event.target.value})

        // newList={
        //     ...newList,
        //     [event.target.name]: event.target.name==="date" ? new Date (event.target.value) : event.target.value
        // }
    }
    return (
        <>
        <div className="form">
        <form onSubmit={submitHandler} onChange={handleChange}>
            <label htmlFor="">Id</label>
            <input type="text" name="id" /> 
            <label htmlFor="">Title</label>
            <input type="text" name="title" />
            <label htmlFor="">Price</label>
            <input type="number" name="price"/>
            <label htmlFor="">Date</label>
            <input type="date" name="date"/>
            <button type="submit">Submit</button>
        </form>
        </div>
        </>
    )
}
